const ChartConfig={colors:{primary:"#1a5a96",secondary:"#0d4275",highlight:"#27ae60",warning:"#e74c3c",neutral:"#3498db",chart:["#1a5a96","#e74c3c","#e67e22","#f39c12","#2ecc71","#3498db","#9b59b6","#34495e","#16a085","#27ae60"]},fonts:{family:'"Nunito", sans-serif',sizes:{title:"18px",subtitle:"14px",axis:"12px",tooltip:"12px"}},defaultOptions:{animation:{enabled:true,easing:"easeinout",speed:800,dynamicAnimation:{enabled:true,speed:350}},toolbar:{show:true,tools:{download:true,selection:false,zoom:false,zoomin:false,zoomout:false,pan:false,reset:false}}},getVendorColor:function(vendorId,opacity=1){const vendorColorMap={portnox:0,cisco:1,aruba:2,forescout:3,fortinac:4,juniper:5,securew2:6,microsoft:7,arista:8,foxpass:9};const colorIndex=vendorColorMap[vendorId]!==undefined?vendorColorMap[vendorId]:0;const color=this.colors.chart[colorIndex];if(opacity<1){const hex=color.replace("#","");const r=parseInt(hex.substring(0,2),16);const g=parseInt(hex.substring(2,4),16);const b=parseInt(hex.substring(4,6),16);return`rgba(${r}, ${g}, ${b}, ${opacity})`}return color},formatCurrency:function(value){return"$"+value.toLocaleString()},formatPercentage:function(value){return value+"%"}};window.ChartConfig=ChartConfig;