class ChartLoader{constructor(){this.apexChartManager=null;this.highchartsManager=null;this.d3Manager=null;this.activeCharts={}}init(){this.apexChartManager=new ApexChartManager;this.highchartsManager=new HighchartsManager;this.d3Manager=new D3Manager;return this}loadExecutiveCharts(resultsData){console.log("Loading executive charts...");this.activeCharts.executive={...this.apexChartManager.initExecutiveCharts(resultsData),...this.d3Manager.initExecutiveCharts(resultsData)};return this.activeCharts.executive}loadFinancialCharts(resultsData){console.log("Loading financial charts...");this.activeCharts.financial={...this.apexChartManager.initFinancialCharts(resultsData)};return this.activeCharts.financial}loadSecurityCharts(resultsData){console.log("Loading security charts...");this.activeCharts.security={...this.highchartsManager.initSecurityCharts(resultsData),...this.d3Manager.initSecurityCharts(resultsData)};return this.activeCharts.security}loadTechnicalCharts(resultsData){console.log("Loading technical charts...");this.activeCharts.technical={...this.highchartsManager.initTechnicalCharts(resultsData)};return this.activeCharts.technical}reloadChartsForView(view,resultsData){switch(view){case"executive":return this.loadExecutiveCharts(resultsData);case"financial":return this.loadFinancialCharts(resultsData);case"security":return this.loadSecurityCharts(resultsData);case"technical":return this.loadTechnicalCharts(resultsData);default:console.warn(`Unknown view: ${view}`);return{}}}destroyAllCharts(){if(this.apexChartManager){this.apexChartManager.destroyCharts(["tcoComparisonChart","cumulativeCostChart","roiChart","valueDriversChart","costStructureChart","costProjectionChart"])}if(this.highchartsManager){this.highchartsManager.destroyCharts(["riskComparisonChart","breachImpactChart","insuranceImpactChart","nistFrameworkChart","architectureChart","featureRadarChart"])}if(this.d3Manager){this.d3Manager.destroyCharts(["securityHeatmap","riskHeatmap","vendorRadarChart"])}this.activeCharts={}}}if(typeof module!=="undefined"&&module.exports){module.exports={ChartLoader:ChartLoader}}