document.addEventListener("DOMContentLoaded",function(){initHeaderParticles();initEnhancedSidebar()});function initHeaderParticles(){if(typeof particlesJS==="undefined"){console.warn("ParticlesJS not found. Header particles will not be initialized.");return}const particlesContainer=document.getElementById("particles-header");if(!particlesContainer){console.warn("Header particles container not found.");return}particlesJS("particles-header",{particles:{number:{value:30,density:{enable:true,value_area:800}},color:{value:"#ffffff"},shape:{type:"circle",stroke:{width:0,color:"#000000"}},opacity:{value:.3,random:true,anim:{enable:false,speed:1,opacity_min:.1,sync:false}},size:{value:3,random:true,anim:{enable:false,speed:40,size_min:.1,sync:false}},line_linked:{enable:true,distance:150,color:"#ffffff",opacity:.2,width:1},move:{enable:true,speed:2,direction:"none",random:false,straight:false,out_mode:"out",bounce:false,attract:{enable:false,rotateX:600,rotateY:1200}}},interactivity:{detect_on:"canvas",events:{onhover:{enable:false},onclick:{enable:false},resize:true}},retina_detect:true});console.log("Header particles initialized successfully.")}function initEnhancedSidebar(){const sidebar=document.getElementById("sidebar");const sidebarToggle=document.getElementById("sidebar-toggle");const contentArea=document.querySelector(".content-area");if(!sidebar||!sidebarToggle||!contentArea){console.warn("Sidebar elements not found. Sidebar toggle will not work.");return}sidebarToggle.addEventListener("click",function(){sidebar.classList.toggle("collapsed");sidebarToggle.classList.toggle("collapsed");contentArea.classList.toggle("expanded")});const configCards=document.querySelectorAll(".config-card");configCards.forEach(card=>{const content=card.querySelector(".config-card-content");if(content){content.style.display="block";content.style.maxHeight="100%";content.style.overflow="visible";content.style.opacity="1"}});console.log("Enhanced sidebar initialized successfully.")}