(function(){function enhanceDashboardCards(){const dashboardCards=document.querySelectorAll(".dashboard-card");dashboardCards.forEach((card,index)=>{card.style.opacity="0";card.style.transform="translateY(20px)";setTimeout(()=>{card.style.transition="opacity 0.5s ease, transform 0.5s ease";card.style.opacity="1";card.style.transform="translateY(0)"},100*index)});const metricValues=document.querySelectorAll(".metric-value");metricValues.forEach(value=>{value.animate([{opacity:.7,transform:"scale(0.95)"},{opacity:1,transform:"scale(1)"}],{duration:800,easing:"ease-out",fill:"forwards"})})}function enhanceChartContainers(){const chartContainers=document.querySelectorAll(".chart-container");chartContainers.forEach((container,index)=>{container.style.opacity="0";container.style.transform="translateY(20px)";setTimeout(()=>{container.style.transition="opacity 0.5s ease, transform 0.5s ease";container.style.opacity="1";container.style.transform="translateY(0)"},300+100*index)})}function enhanceTabs(){const mainTabs=document.querySelectorAll(".main-tab");const resultsTabs=document.querySelectorAll(".results-tab");mainTabs.forEach(tab=>{tab.addEventListener("mouseenter",()=>{if(!tab.classList.contains("active")){tab.style.backgroundColor="rgba(26, 90, 150, 0.05)"}});tab.addEventListener("mouseleave",()=>{if(!tab.classList.contains("active")){tab.style.backgroundColor=""}})});resultsTabs.forEach(tab=>{tab.addEventListener("mouseenter",()=>{if(!tab.classList.contains("active")){tab.style.color="#1a5a96"}});tab.addEventListener("mouseleave",()=>{if(!tab.classList.contains("active")){tab.style.color=""}})})}function initEnhancements(){enhanceDashboardCards();enhanceChartContainers();enhanceTabs()}if(document.readyState==="loading"){document.addEventListener("DOMContentLoaded",initEnhancements)}else{initEnhancements()}document.addEventListener("viewChanged",initEnhancements)})();