class ThemeManager{constructor(){this.themes={light:{primaryColor:"#1a5a96",secondaryColor:"#2ecc71",accentColor:"#f39c12",backgroundColor:"#f9f9f9",cardBackground:"#ffffff",textColor:"#333333",borderColor:"#dddddd"},dark:{primaryColor:"#2980b9",secondaryColor:"#27ae60",accentColor:"#e67e22",backgroundColor:"#121212",cardBackground:"#1e1e1e",textColor:"#f5f5f5",borderColor:"#333333"}};this.currentTheme="light";this.systemPrefersDark=false;this.initVariables();this.checkSystemPreference();this.setupListeners()}initVariables(){const theme=this.themes[this.currentTheme];this.setCssVariables(theme)}checkSystemPreference(){if(window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches){this.systemPrefersDark=true;const savedTheme=localStorage.getItem("portnox-theme");if(!savedTheme){this.setTheme("dark")}}window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",e=>{this.systemPrefersDark=e.matches;const savedTheme=localStorage.getItem("portnox-theme");if(!savedTheme){this.setTheme(this.systemPrefersDark?"dark":"light")}})}setupListeners(){const darkModeToggle=document.getElementById("dark-mode-toggle");if(darkModeToggle){darkModeToggle.addEventListener("click",()=>{this.toggleDarkMode()})}const savedTheme=localStorage.getItem("portnox-theme");if(savedTheme){this.setTheme(savedTheme)}}toggleDarkMode(){const newTheme=this.currentTheme==="light"?"dark":"light";this.setTheme(newTheme)}setTheme(themeName){if(!this.themes[themeName])return;this.currentTheme=themeName;localStorage.setItem("portnox-theme",themeName);if(themeName==="dark"){document.body.classList.add("dark-mode");document.body.classList.remove("light-mode")}else{document.body.classList.add("light-mode");document.body.classList.remove("dark-mode")}this.setCssVariables(this.themes[themeName]);const darkModeToggle=document.getElementById("dark-mode-toggle");if(darkModeToggle){const icon=darkModeToggle.querySelector("i");if(icon){icon.className=themeName==="dark"?"fas fa-sun":"fas fa-moon"}}window.dispatchEvent(new CustomEvent("themechange",{detail:{theme:themeName}}))}setCssVariables(theme){const root=document.documentElement;for(const[key,value]of Object.entries(theme)){const cssVar=key.replace(/([A-Z])/g,"-$1").toLowerCase();root.style.setProperty(`--${cssVar}`,value)}}getCurrentTheme(){return this.currentTheme}isDarkMode(){return this.currentTheme==="dark"}}if(typeof module!=="undefined"&&module.exports){module.exports={ThemeManager:ThemeManager}}